&lt;&#x2F;dependency&gt;

&lt;dependency&gt;
            &lt;groupId&gt;junit&lt;&#x2F;groupId&gt;
            &lt;artifactId&gt;junit&lt;&#x2F;artifactId&gt;
            &lt;scope&gt;test&lt;&#x2F;scope&gt;
        &lt;&#x2F;dependency&gt;



第一步：mysql中建表（sound表）
SET NAMES utf8mb4;SET FOREIGN_KEY_CHECKS &#x3D; 0;

DROP TABLE IF EXISTS sound;CREATE TABLE sound (  id int NOT NULL AUTO_INCREMENT,  xuHao1 bigint DEFAULT NULL,  xuHao2 bigint DEFAULT NULL,  xuHao3 bigint DEFAULT NULL,  xuHao4 bigint DEFAULT NULL,  xuHao5 bigint DEFAULT NULL,  xuHao6 bigint DEFAULT NULL,  xuHao7 bigint DEFAULT NULL,  xuHao8 bigint DEFAULT NULL,  xuHao9 bigint DEFAULT NULL,  xuHao10 bigint DEFAULT NULL,  xuHao11 bigint DEFAULT NULL,  xuHao12 bigint DEFAULT NULL,  PRIMARY KEY (id)) ENGINE&#x3D;InnoDB AUTO_INCREMENT&#x3D;4 DEFAULT CHARSET&#x3D;utf8mb4 COLLATE&#x3D;utf8mb4_0900_ai_ci;

BEGIN;INSERT INTO sound VALUES (1, 2, 3, 4, 1, 1, 1, 1, 1, 1, 1, 1, 1);INSERT INTO sound VALUES (2, 2, 3, 4, 1, 1, 1, 1, 1, 1, 1, 1, 1);INSERT INTO sound VALUES (3, 2, 3, 4, 1, 1, 1, 1, 1, 1, 1, 1, 1);COMMIT;
SET FOREIGN_KEY_CHECKS &#x3D; 1;
第二步：配置 application.yml 文件
server:
  port: 8080
spring:
  application:
    name: sound_velocity_experiment
  datasource:
    druid:
      driver-class-name: com.mysql.cj.jdbc.Driver
      url: jdbc:mysql:&#x2F;&#x2F;localhost:3306&#x2F;test?serverTimezone&#x3D;Asia&#x2F;Shanghai&amp;useUnicode&#x3D;true&amp;characterEncoding&#x3D;utf-8&amp;zeroDateTimeBehavior&#x3D;convertToNull&amp;useSSL&#x3D;false&amp;allowPublicKeyRetrieval&#x3D;true
      username: root
      password: root

mybatis-plus:
  configuration:
    #在映射实体或者属性时，将数据库中表名和字段名中的下划线去掉，按照驼峰命名法映射
    map-underscore-to-camel-case: false
    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl
  global-config:
    db-config:
   # 分配id  主键递增(如果没配置，需在对应的实体类中的字段上加入 @TableId(type &#x3D; IdType.AUTO) )
      id-type: ASSIGN_ID  



第三步：在 entity 中配置实体类，对应数据库中的表
@TableName(&quot;sound&quot;)  &#x2F;&#x2F; 对应数据库中的表名
@AllArgsConstructor  &#x2F;&#x2F; 全参构造器
@NoArgsConstructor   &#x2F;&#x2F; 无参构造器
@Data  &#x2F;&#x2F; lombok会自动生成GETTER和SETTER方法
public class Sound &#123;
    private Integer id;
    private Long xuHao1;
    private Long xuHao2;
    private Long xuHao3;
    private Long xuHao4;
    private Long xuHao5;
    private Long xuHao6;
    private Long xuHao7;
    private Long xuHao8;
    private Long xuHao9;
    private Long xuHao10;
    private Long xuHao11;
    private Long xuHao12;
&#125;



第四步： 在 Soundmapper 类，操控数据库的地方，继承BaseMapper接口
&#x2F;**
 * 继承BaseMapper&lt;Sound&gt;接口，相当于帮你写好通用的CRUD方法
 *&#x2F;
@Mapper
public interface  SoundMapper extends BaseMapper&lt;Sound&gt; &#123;&#125;



第五步： 启动类上加Mapper扫描注解
@Slf4j
@MapperScan(&quot;com&#x2F;areay&#x2F;mapper&quot;)  &#x2F;&#x2F; 指定要扫描mapper的位置
@SpringBootApplication
public class SoundVelocityExperimentApplication &#123;
    public static void main(String[] args) &#123;   SpringApplication.run(SoundVelocityExperimentApplication.class, args);
        log.info(&quot;项目启动成功...&quot;);
    &#125;
&#125;



第六步：测试类中写入增删改查测试方法
@SpringBootTest
@Slf4j
@DisplayName(&quot;测试mybatis-plus操作&quot;)
class SoundVelocityExperimentApplicationTests &#123;
    &#x2F;**
     * 注入实体类
     *&#x2F;
    @Autowired
    SoundMapper soundMapper;

    &#x2F;**
     * 查询所有
     *&#x2F;
    @Test
    void testselectAll()&#123;
        &#x2F;&#x2F;         查,selectList获取所有的用户信息
        List&lt;Sound&gt; sounds &#x3D; soundMapper.selectList(null);
        sounds.forEach(System.out::println);
    &#125;

    &#x2F;**
     * 更新方法
     *&#x2F;
    @Test
    void testupDate()&#123;
        Sound sound &#x3D; new Sound(1, 21L, 4L, 5L, 6L, 7L, 8L, 9L, 10L, 11L, 12L, 13L,12L);
        int update &#x3D; soundMapper.update(sound, null);
        System.out.println(update);  &#x2F;&#x2F; 返回1说明更新成功
    &#125;


    &#x2F;**
     * 插入方法
     *&#x2F;
    @Test
    void testInsert()&#123;
        Sound sound1 &#x3D; new Sound();
        sound1.setId(3);
        sound1.setXuHao1(1L);
        sound1.setXuHao2(1L);
        sound1.setXuHao3(1L);
        sound1.setXuHao4(1L);
        sound1.setXuHao5(1L);
        sound1.setXuHao6(1L);
        sound1.setXuHao7(1L);
        sound1.setXuHao8(1L);
        sound1.setXuHao9(1L);
        sound1.setXuHao10(1L);
        sound1.setXuHao11(1L);
        sound1.setXuHao12(1L);
        int insert &#x3D; soundMapper.insert(sound1);
&#x2F;&#x2F;        打印日志，方便观察
        log.info(&quot;insert &#x3D;&gt; &quot; ,insert);
    &#125;

    &#x2F;**
     * 删除方法
     *&#x2F;
    @Test
    void testDelete()&#123;
        int delete &#x3D; soundMapper.deleteById(2);   &#x2F;&#x2F;  指定实体类或者id
        log.warn(&quot;delete &#x3D;&gt; &quot; , delete);
    &#125;
&#125;



第七步： 在 Controller 类中写入对应的业务
&#x2F;**
 * 表的控制器
 *&#x2F;

@RestController
@RequestMapping(&quot;&#x2F;sound&quot;)
public class SoundController &#123;

    @Autowired
    private SoundMapper soundMapper;


    &#x2F;**
     * 返回表中所有用户
     * @return
     *&#x2F;
    @GetMapping
    public List&lt;Sound&gt; getSounds()&#123;
        return soundMapper.selectList(null);
    &#125;

    &#x2F;**
     * 通过传来的数据，插入用户数据
     * @param sound
     * @return
     *&#x2F;
    @PostMapping
    public Boolean saveSound(@RequestBody Sound sound)&#123;
        int i &#x3D; soundMapper.insert(sound);
        return i &#x3D;&#x3D; 1;
    &#125;

    &#x2F;**
     * 通过传来的数据，更新表
     * @param sound
     * @return
     *&#x2F;
    @PutMapping
    public Boolean updateSound(@RequestBody Sound sound)&#123;
        int update &#x3D; soundMapper.updateById(sound);
        return update &#x3D;&#x3D; 1;
    &#125;

    &#x2F;**
     * 通过用户id删除用户
     * @param id
     * @return
     *&#x2F;
    @DeleteMapping
    public Boolean deleteSound(@RequestParam Integer id)&#123;
        int deleteById &#x3D; soundMapper.deleteById(id);
        return deleteById &#x3D;&#x3D; 1;
    &#125;
&#125;

